mapper.update:
UPDATE wk.wk_account  SET update_time =  #{updateTime}
 , account_name =  #{accountName}
 , user_name =  #{userName}
 , password =  #{password}
 , card_no =  #{cardNo}
 , web_user_name =  #{webUserName}
 , web_password =  #{webPassword}
 , seg =  #{seg}
 , opt_id =  #{optId} 
WHERE id=#{id}

mapper.select:
SELECT id, account_name accountName, user_name userName, password, card_no cardNo, web_user_name webUserName, web_password webPassword, seg, create_time createTime, update_time updateTime, opt_id optId FROM wk.wk_account

     AND  account_name =  #{accountName}
     AND  user_name =  #{userName}
     AND  password =  #{password}
     AND  card_no =  #{cardNo}
     AND  web_user_name =  #{webUserName}
     AND  web_password =  #{webPassword}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
  ORDER BY seg DESC, update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_account

     AND  account_name =  #{accountName}
     AND  user_name =  #{userName}
     AND  password =  #{password}
     AND  card_no =  #{cardNo}
     AND  web_user_name =  #{webUserName}
     AND  web_password =  #{webPassword}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
