mapper.update:
UPDATE wk.wk_card SET update_time =  #{updateTime}
 , self =  #{self}
 , customer_id =  #{customerId}
 , card_name =  #{cardName}
 , name =  #{name}
 , id_number =  #{idNumber}
 , phone =  #{phone}
 , bank_name =  #{bankName}
 , card_number =  #{cardNumber}
 , password =  #{password}
 , bill_date =  #{billDate}
 , replay_date =  #{replayDate}
 , valid_date =  #{validDate}
 , total =  #{total}
 , bill =  #{bill}
 , replay_rate =  #{replayRate}
 , mini_fee =  #{miniFee}
 , cash_rate =  #{cashRate}
 , income =  #{income}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id =  #{optId}  
WHERE id=#{id}

mapper.select:
SELECT
id,
self,
customer_id customerId,
card_name cardName,
name,
id_number idNumber,
phone,
bank_name bankName,
card_number cardNumber,
password,
bill_date billDate,
replay_date replayDate,
valid_date validDate,
total,
bill,
replay_rate replayRate,
mini_fee miniFee,
cash_rate cashRate,
income, remark, seg, create_time createTime, update_time updateTime, opt_id optId FROM wk.wk_card

     AND  self =  #{self}
     AND  card_name =  #{cardName}
     AND  name =  #{name}
     AND  id_number =  #{idNumber}
     AND  phone =  #{phone}
     AND  bank_name =  #{bankName}
     AND  card_number =  #{cardNumber}
     AND  password =  #{password}
     AND  bill_date =  #{billDate}
     AND  replay_date =  #{replayDate}
     AND  total =  #{total}
     AND  bill =  #{bill}
     AND  replay_rate =  #{replayRate}
     AND  mini_fee =  #{miniFee}
     AND  cash_rate =  #{cashRate}
     AND  income =  #{income}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId} 
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}

        AND
        (
            name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
            card_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
            phone LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
            id_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
            bank_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
            card_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )

  ORDER BY seg DESC, update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_card

     AND  self =  #{self}
     AND  card_name =  #{cardName}
     AND  name =  #{name}
     AND  id_number =  #{idNumber}
     AND  phone =  #{phone}
     AND  bank_name =  #{bankName}
     AND  card_number =  #{cardNumber}
     AND  password =  #{password}
     AND  bill_date =  #{billDate}
     AND  replay_date =  #{replayDate}
     AND  total =  #{total}
     AND  bill =  #{bill}
     AND  replay_rate =  #{replayRate}
     AND  mini_fee =  #{miniFee}
     AND  cash_rate =  #{cashRate}
     AND  income =  #{income}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId} 
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}

        AND
        (
        name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        card_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        phone LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        id_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        bank_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        card_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )
