mapper.update:
UPDATE wk.wk_model SET update_time =  #{update_time}
 , name =  #{name}
 , remark =  #{remark}
 , icon =  #{icon}
 , seg =  #{seg}
 , opt_id =  #{opt_id} 
WHERE id=#{id}

mapper.select:
SELECT id, name, remark, seg, create_time, update_time, opt_id FROM wk.wk_model

     AND  name =  #{name}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{opt_id}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
 ORDER BY seg ASC, update_time DESC   limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_model

     AND  name =  #{name}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{opt_id}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
