mapper.update:
UPDATE wk.wk_order SET update_time =  #{updateTime}
 , user_id =  #{userId}
 , type =  #{type}
 , total =  #{total}
 , rate =  #{rate}
 , fee =  #{fee}
 , discount =  #{discount}
 , real_fee =  #{realFee}
 , status =  #{status}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id) =  #{optId}  
WHERE id=(#{id}

mapper.select:
SELECT id, user_id userId, type, total, rate, fee, discount, real_fee realFee, status, remark, seg, create_time createTime, update_time updateTime, opt_id optId FROM wk.wk_order

     AND  user_id =  #{userId}
     AND  type =  #{type}
     AND  total =  #{total}
     AND  rate =  #{rate}
     AND  fee =  #{fee}
     AND  discount =  #{discount}
     AND  real_fee =  #{realFee}
     AND  status =  #{status}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
  ORDER BY seg DESC, update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_order

     AND  user_id =  #{userId}
     AND  type =  #{type}
     AND  total =  #{total}
     AND  rate =  #{rate}
     AND  fee =  #{fee}
     AND  discount =  #{discount}
     AND  real_fee =  #{realFee}
     AND  status =  #{status}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
