mapper.update:
UPDATE wk.wk_order_import SET update_time =  #{updateTime}
,id = #{id}
, order_id  = #{orderId}
, export_date = #{exportDate}
, type = #{type}
, pos_id = #{posId}
, mall_name = #{mallName}
, consume_account_id = #{consumeAccountId}
, bill = #{bill}
, consume_type = #{consumeType}
, result = #{result}
, rate = #{rate}
, fee = #{fee}
, import_bill = #{importBill}
, should_bill = #{shouldBill}
, remark = #{remark}
, seg = #{seg}
, opt_id = #{optId}
WHERE id=#{id}

mapper.select:
SELECT
id id,
order_id  orderId,
export_date exportDate,
type type,
pos_id posId,
mall_name mallName,
consume_account_id consumeAccountId,
bill bill,
consume_type consumeType,
result result,
rate rate,
fee fee,
import_bill importBill,
should_bill shouldBill,
remark remark,
seg seg,
create_time createTime,
update_time updateTime,
opt_id optId
FROM wk.wk_order_import


    AND  order_id  = #{orderId}
    AND  export_date = #{exportDate}
    AND  type = #{type}
    AND  pos_id = #{posId}
    AND  mall_name = #{mallName}
    AND  consume_account_id = #{consumeAccountId}
    AND  bill = #{bill}
    AND  consume_type = #{consumeType}
    AND  result = #{result}
    AND  rate = #{rate}
    AND  fee = #{fee}
    AND  import_bill = #{importBill}
    AND  should_bill = #{shouldBill}

    AND  remark = #{remark} 
    AND  seg = #{seg} 
    AND  opt_id = #{ optId }
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}

        AND
        (
        mallName LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        remark LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )

  ORDER BY  update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*)   FROM wk.wk_order_import

    AND  order_id  = #{orderId}
    AND  export_date = #{exportDate}
    AND  type = #{type}
    AND  pos_id = #{posId}
    AND  mall_name = #{mallName}
    AND  consume_account_id = #{consumeAccountId}
    AND  bill = #{bill}
    AND  consume_type = #{consumeType}
    AND  result = #{result}
    AND  rate = #{rate}
    AND  fee = #{fee}
    AND  import_bill = #{importBill}
    AND  should_bill = #{shouldBill}

    AND  remark = #{remark} 
    AND  seg = #{seg} 
    AND  opt_id = #{ optId }
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}

        AND
        (
        mallName LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        remark LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )
