mapper.select:
SELECT id,name,remark,seg,create_time,update_time,opt_id FROM wk_role

     AND name = #{name}
     AND remark = #{remark}
     AND seg = #{seg}
     AND opt_id = #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
  limit #{pageStart},#{pageSize}
