mapper.update:
UPDATE wk.wk_worker SET update_time =  #{updateTime}
 , org_id =  #{orgId}
 , role_id =  #{roleId}
 , user_name =  #{userName}
 , password =  #{password}
 , name =  #{name}
 , id_number =  #{idNumber}
 , phone =  #{phone}
 , weixin =  #{weixin}
 , font_img =  #{fontImg}
 , after_img =  #{afterImg}
 , home_img =  #{homeImg}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id =  #{optId}
WHERE id=#{id}

mapper.select:
SELECT id, org_id orgId, role_id roleId, user_name userName,  name, id_number idNumber, phone, weixin, font_img fontImg, after_img afterImg, home_img homeImg, remark, seg,create_time createTime, update_time updateTime, opt_id optId  FROM wk.wk_worker

     AND  org_id =  #{orgId}
     AND  role_id =  #{roleId}
     AND  user_name =  #{userName}
     AND  password =  #{password}
     AND  name =  #{name}
     AND  id_number =  #{idNumber}
     AND  phone =  #{phone}
     AND  weixin =  #{weixin}
     AND  font_img =  #{fontImg}
     AND  after_img =  #{afterImg}
     AND  home_img =  #{homeImg}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
 ORDER BY seg DESC, update_time DESC   limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_worker

     AND  org_id =  #{orgId}
     AND  role_id =  #{roleId}
     AND  user_name =  #{userName}
     AND  password =  #{password}
     AND  name =  #{name}
     AND  id_number =  #{idNumber}
     AND  phone =  #{phone}
     AND  weixin =  #{weixin}
     AND  font_img =  #{fontImg}
     AND  after_img =  #{afterImg}
     AND  home_img =  #{homeImg}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
